INSERT INTO banksters_db.stocks (id, apr, aug, name, currency, december, description, ex_dividend_date, exchange, feb, forward_dividend_and_yield, included_in_index, jan, jul, jun, logo_url, mar, may, min_traded_qty, nov, oct, previous_day_closing_price, sep, ticker_code, year) VALUES ('4d916e14-049d-48f2-bda4-b01711af0442', 126.77, null, 'FedEx Corporation', 'USD', null, 'FedEx Corporation (FedEx) provides a portfolio of transportation, e-commerce and business services through companies competing collectively, operating independently and managed collaboratively, under the FedEx brand. The Company''s segments include FedEx Express, TNT Express, FedEx Ground, FedEx Freight and FedEx Services. The FedEx Express segment offers a range of the United States domestic and international shipping services for delivery of packages and freight. TNT Express segment collects, transports and delivers documents, parcels and freight on a day-definite or time-definite basis. The FedEx Ground segment provides business and residential money-back guaranteed ground package delivery services. The FedEx Freight segment offers less-than-truckload (LTL) freight services. The FedEx Services segment provides its other companies with sales, marketing, information technology, communications, customer service and other back-office support.', 'Jun 25, 2020', 'NYSE', 141.17, '2.60 (1.67%)', 'DJIA, S&P 100, S&P 500 ', 144.64, 158.59, 140.22, 'https://res.cloudinary.com/dyhmxus4n/image/upload/v1594641145/Java%20Project/pdzovmi7olr7qpokngpt.jpg', 124.31, 130.56, '1', null, null, 158.59, null, 'FDX', '2020');
INSERT INTO banksters_db.stocks (id, apr, aug, name, currency, december, description, ex_dividend_date, exchange, feb, forward_dividend_and_yield, included_in_index, jan, jul, jun, logo_url, mar, may, min_traded_qty, nov, oct, previous_day_closing_price, sep, ticker_code, year) VALUES ('631e2486-0337-4aa1-bdab-60fba2a6b64a', 57.45, null, 'Verizon Communications Inc.', 'USD', null, 'Verizon Communications Inc. is a holding company. The Company, through its subsidiaries, provides communications, information and entertainment products and services to consumers, businesses and governmental agencies. Its reportable segments are Verizon Consumer Group and Verizon Business Group. Its Consumer segment provides wireless and wireline communications services. Its wireless services are provided across wireless networks in the United States under the Verizon Wireless brand. Its wireline services are provided in nine states in the Mid-Atlantic and Northeastern United States, over its 100% fiber-optic network under the Fios brand and via traditional copper-based network. Its Business segment provides wireless and wireline communications services and products, video and data services, corporate networking solutions, security and managed network services, local and long-distance voice services and network access to deliver various Internet of Things (IoT) services and products.', 'Jul 08, 2020', 'NYSE', 54.16, '2.46 (4.56%)', 'DJIA, S&P 100, S&P 500', 59.44, 54.49, 55.13, 'https://res.cloudinary.com/dyhmxus4n/image/upload/v1594640107/Java%20Project/gk99iq446fty4veuwhxx.jpg', 53.73, 57.38, '1', null, null, 54.49, null, 'VZ', '2020');
INSERT INTO banksters_db.stocks (id, apr, aug, name, currency, december, description, ex_dividend_date, exchange, feb, forward_dividend_and_yield, included_in_index, jan, jul, jun, logo_url, mar, may, min_traded_qty, nov, oct, previous_day_closing_price, sep, ticker_code, year) VALUES ('7e99ad1b-7cde-4f5a-8f6e-26470b0ce377', 72.55, null, 'Starbucks Corporation ', 'USD', null, 'Starbucks Corporation is a roaster, marketer and retailer of coffee. The Company operates in 75 countries. The Company operates through four segments: Americas, which is inclusive of the United States, Canada, and Latin America; China/Asia Pacific (CAP); Europe, Middle East, and Africa (EMEA), and Channel Development. The Company''s Americas, CAP, and EMEA segments include both company-operated and licensed stores. Its Channel Development segment includes roasted whole bean and ground coffees, Tazo teas, Starbucks- and Tazo-branded single-serve products, a range of ready-to-drink beverages, such as Frappuccino, Starbucks Doubleshot and Starbucks Refreshers beverages and other branded products sold across the world through channels, such as grocery stores, warehouse clubs, specialty retailers, convenience stores and the United States foodservice accounts. As of October 30, 2019, the company operated approximately 31,000 stores. Starbucks Corporation was founded in 1971 and is based in Seattle, Washington.', 'Aug 05, 2020', 'NASDAQ', 78.43, '1.64 (2.23%)', 'NASDAQ 100,  S&P 100,  S&P 500 ', 84.83, 74.29, 73.59, 'https://res.cloudinary.com/dyhmxus4n/image/upload/v1592766637/Java%20Project/llip6px1ltlisj6g1jsg.jpg', 65.74, 77.99, '1', null, null, 74.29, null, 'SBUX', '2020');
INSERT INTO banksters_db.stocks (id, apr, aug, name, currency, december, description, ex_dividend_date, exchange, feb, forward_dividend_and_yield, included_in_index, jan, jul, jun, logo_url, mar, may, min_traded_qty, nov, oct, previous_day_closing_price, sep, ticker_code, year) VALUES ('97d392eb-64ab-4471-8394-cfae6f2bc2b6', 121.55, null, 'Walmart Inc.', 'USD', null, 'Walmart Inc. was founded in 1945 and is based in Bentonville, Arkansas. It engages in the retail and wholesale and operates supercenters, supermarkets, hypermarkets, warehouse clubs, cash and carry stores, discount stores, drugstores, and convenience stores. The company offers grocery products, including meat, produce, natural and organics, deli and bakery, dairy, frozen foods, alcoholic and nonalcoholic beverages, and floral and dry grocery, as well as consumables, such as health and beauty aids, baby products, household chemicals, paper goods, and pet supplies; and health and wellness products. It also provides electronics, cameras and supplies, photo processing services, wireless, movies, music, video games, and books; stationery, automotive, hardware and paint, sporting goods, and outdoor living and horticulture; apparel, shoes, jewelry, and accessories; home furnishings, housewares and small appliances, bedding, home decor, toys, fabrics, crafts, and seasonal merchandise, as well as brand name merchandise.', 'Aug 12, 2020', 'NYSE', 107.68, '2.16 (1.69%)', 'DJIA,  S&P 100,  S&P 500', 114.49, 130.68, 119.78, 'https://res.cloudinary.com/dyhmxus4n/image/upload/v1594593456/Java%20Project/fcfifwikoudt0acih43v.jpg', 113.62, 124.06, '1', null, null, 130.68, null, 'WMT', '2020');
