INSERT INTO banksters_db.stocks (id, apr, aug, name, currency, december, description, ex_dividend_date, exchange, feb, forward_dividend_and_yield, included_in_index, jan, jul, jun, logo_url, mar, may, min_traded_qty, nov, oct, previous_day_closing_price, sep, ticker_code, year) VALUES ('b3ed0947-1621-463c-8544-db0eebc72f88', 303.00, null, 'Costco Wholesale Corporation', 'USD', null, 'Costco Wholesale Corporation operates membership warehouses in the United States, Puerto Rico, Canada, the United Kingdom, Mexico, Japan, Korea, Australia, Spain, France, Iceland, China, and Taiwan. It offers branded and private-label products in a range of merchandise categories. The company provides dry and packaged foods, and groceries; snack foods, candies, alcoholic and nonalcoholic beverages, and cleaning supplies; appliances, electronics, health and beauty aids, hardware, and garden and patio products; meat, bakery, and deli products, as well as produce; and apparel and small appliances. It also operates gas stations, pharmacies, optical dispensing centers, food courts, and hearing-aid centers; and offers business delivery, travel, and various other services online in various countries. As of September 1, 2019, the company operated 782 warehouses, including 543 in the United States. Costco Wholesale Corporation was founded in 1976 and is based in Issaquah, Washington.', 'Apr 29, 2020', 'NASDAQ', 281.14, '2.80 (0.86%)', 'NASDAQ-100, S&P 100, S&P 500', 305.52, 327.41, 303.21, 'https://res.cloudinary.com/dyhmxus4n/image/upload/v1594651877/Java%20Project/pqpzwakziuqufcld7zp6.png', 285.13, 308.47, '1', null, null, 327.24, null, 'COST', '2020');
INSERT INTO banksters_db.stocks (id, apr, aug, name, currency, december, description, ex_dividend_date, exchange, feb, forward_dividend_and_yield, included_in_index, jan, jul, jun, logo_url, mar, may, min_traded_qty, nov, oct, previous_day_closing_price, sep, ticker_code, year) VALUES ('bf7e04c5-a824-46ba-ba83-6e47e499dc1c', 42.38, null, 'Cisco Systems Inc.', 'USD', null, 'Cisco Systems, Inc., is engaged in designing and selling a range of technologies across networking, security, collaboration, applications and the cloud. It operates through three geographic segments: Americas; Europe, Middle East, and Africa; and Asia Pacific, Japan, and China. Its product and technologies includes infrastructure platforms; applications; security and other products. It also offers technical support services and advanced services. Infrastructure Platforms consists of its core networking technologies of switching, routing, data center products and wireless that are designed to work together to deliver networking capabilities and transport and store data. Application product category consists primarily of software-related offerings that utilize the core networking and data center platforms to provide their functions. Security product category primarily includes Company’s unified threat management products, advanced threat security products, and web security products.', 'Jul 01, 2020', 'NASDAQ', 39.93, '1.44 (3.08%)', 'NASDAQ-100,  DJIA,  S&P 100,  S&P 500 ', 45.97, 46.66, 46.64, 'https://res.cloudinary.com/dyhmxus4n/image/upload/v1594640499/Java%20Project/lwxb9rkzqgsltrwgsi4z.jpg', 39.31, 47.82, '1', null, null, 46.66, null, 'CSCO', '2020');
INSERT INTO banksters_db.stocks (id, apr, aug, name, currency, december, description, ex_dividend_date, exchange, feb, forward_dividend_and_yield, included_in_index, jan, jul, jun, logo_url, mar, may, min_traded_qty, nov, oct, previous_day_closing_price, sep, ticker_code, year) VALUES ('c6bf9bf0-14db-4eeb-af51-0b5aedc8abfd', 30.47, null, 'AT&T Inc.', 'USD', null, 'AT&T Inc. is a holding company. The Company is a provider of telecommunications, media and technology services globally. The Company operates through four segments: Communication segment, WarnerMedia segment, Latin America segment and Xandr segment. The Communications segment provides wireless and wireline telecom, video and broadband services to consumers.The business units of the Communication segment includes Mobility, Entertainment Group and Business Wireline. The WarnerMedia segment develops, produces and distributes feature films, television, gaming and other content over various physical and digital formats. The business units of the WarnerMedia segment includes Turner, Home Box Office and Warner Bros. Latin America segment provides entertainment services in Latin America and wireless services in Mexico. Viro and Mexico are the business units of the Latin America segment. The Xandr segment provides advertising services.', 'Jul 08, 2020', 'NYSE', 35.22, '2.08 (7.04%)', 'S&P 100, S&P 500 ', 37.62, 29.98, 30.23, 'https://res.cloudinary.com/dyhmxus4n/image/upload/v1594650819/Java%20Project/ql9hhtiuoccp06ozxckb.webp', 29.15, 30.86, '1', null, null, 30.01, null, 'T', '2020');
