INSERT INTO banksters_db.stocks (id, apr, aug, name, currency, december, description, ex_dividend_date, exchange, feb, forward_dividend_and_yield, included_in_index, jan, jul, jun, logo_url, mar, may, min_traded_qty, nov, oct, previous_day_closing_price, sep, ticker_code, year) VALUES ('e5d13872-3624-4556-bbd7-0ad4d15bea7c', 1390.00, null, 'Burberry Group PLC ', 'GBP', null, 'Burberry Group plc, together with its subsidiaries, manufactures, retails, and wholesales luxury goods under the Burberry brand. The company operates in two segments, Retail/Wholesale and Licensing. It offers women, men, and children apparels, as well as accessories. The company also licenses third parties to manufacture and distribute products using the Burberry trademarks. It markets its products through Burberry mainline stores, concessions, outlets, digital commerce, Burberry franchisees, department stores, and multi-brand specialty accounts. As of March 28, 2020, the company operated 218 mainline stores, 149 concession stores, 54 outlets, and 44 franchise stores. It operates in the Asia Pacific, Europe, the Middle East, India, Africa, and the Americas. Burberry Group plc was founded in 1856 and is headquartered in London, the United Kingdom.', 'Dec 18, 2019', 'LSE', 1649.00, 'N/A (N/A)', 'FTSE 100 ', 1953.00, 1550.00, 1599.50, 'https://res.cloudinary.com/dyhmxus4n/image/upload/v1592766820/Java%20Project/gmxgez54yvijaplrqqlc.jpg', 1324.50, 1495.50, '1', null, null, 1550.00, null, 'BRBY', '2020');